import { BrowserRouter as Router, Routes, Route, Outlet, NavLink } from 'react-router-dom';
import Home from './pages/Home';
import Education from './pages/Education';
import Experience from './pages/Experience';
import Contact from './pages/Contact';
import { useState } from 'react';
import './App.css';

const App = () => {
  const [fadeState, setFadeState] = useState('');

  const handleNavLinkClick = () => {
    setFadeState('fade-out');

    // You can add a timeout to remove the fade-out class after the animation completes
    setTimeout(() => {
      setFadeState('fade-in');
    }, 800); // Adjust the timeout duration according to your animation duration
  };

  return (
    <Router>
      <div className="container mx-auto p-4">
        <nav className="bg-gray-800 p-4">
          <ul className="flex space-x-8 text-white">
            <li>
              <NavLink
                to="/"
                exact
                className={({ isActive }) =>
                  isActive
                    ? "font-bold text-yellow-500"
                    : "hover:text-gray-300 font-bold hover:underline text-blue-400"
                }
                onClick={handleNavLinkClick}
              >
                Home
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/education"
                className={({ isActive }) =>
                  isActive
                    ? "font-bold text-yellow-500"
                    : "hover:text-gray-300 font-bold hover:underline text-blue-400"
                }
                onClick={handleNavLinkClick}
              >
                Education
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/experience"
                className={({ isActive }) =>
                  isActive
                    ? "font-bold text-yellow-500"
                    : "hover:text-gray-300 font-bold hover:underline text-blue-400"
                }
                onClick={handleNavLinkClick}
              >
                Experience
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/contact"
                className={({ isActive }) =>
                  isActive
                    ? "font-bold text-yellow-500"
                    : "hover:text-gray-300 font-bold hover:underline text-blue-400"
                }
                onClick={handleNavLinkClick}
              >
                Contact
              </NavLink>
            </li>
          </ul>
        </nav>

        <hr />
        <div className="content">
          <Routes>
            <Route
              path="/"
              element={<Home className={`fade-content ${fadeState}`} />}
            />
            <Route
              path="/education"
              element={<Education className={`fade-content ${fadeState}`} />}
            />
            <Route
              path="/experience"
              element={<Experience className={`fade-content ${fadeState}`} />}
            />
            <Route
              path="/contact"
              element={<Contact className={`fade-content ${fadeState}`} />}
            />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
