import logo from './logo.svg';
// import './App.css';
import { BrowserRouter as Router, Routes, Route, Outlet, NavLink } from 'react-router-dom';
import Home from './pages/Home';
import Education from './pages/Education';
import Experience from './pages/Experience';
import Contact from './pages/Contact';

const App = () => {
  const [fadeIn, setFadeIn] = useState(false);

  const handleNavLinkClick = () => {
    setFadeIn(true);
    // You can add a timeout to remove the fade-in class after the animation completes
    setTimeout(() => {
      setFadeIn(false);
    }, 1000); // Adjust the timeout duration according to your animation duration
  };

  return (
    <Router>
      <div className={`container mx-auto p-4 ${fadeIn ? 'fade-in' : ''}`}>
        <nav className='bg-gray-800 p-4'>
          <ul className='flex space-x-8 text-white'>
            <li>
              <NavLink to="/" exact className={({ isActive }) => isActive ? "font-bold text-yellow-500" : "hover:text-gray-300 font-bold hover:underline text-blue-400" }>Home</NavLink>
            </li>
            <li>
              <NavLink to="/education" className={({ isActive }) => isActive ? "font-bold text-yellow-500" : "hover:text-gray-300 font-bold hover:underline text-blue-400" }>Education</NavLink>
            </li>
            <li>
              <NavLink to="/experience" className={({ isActive }) => isActive ? "font-bold text-yellow-500" : "hover:text-gray-300 font-bold hover:underline text-blue-400" }>Experience</NavLink>
            </li>
            <li>
              <NavLink to="/contact" className={({ isActive }) => isActive ? "font-bold text-yellow-500" : "hover:text-gray-300 font-bold hover:underline text-blue-400" }>Contact</NavLink>
            </li>
          </ul>
        </nav>

      <hr />
      <Outlet />
      <Routes>
        <Route exact path="/" element={<Home />} />
        <Route path="/education" element={<Education />} />
        <Route path="/experience" element={<Experience />} />
        <Route path="/contact" element={<Contact />} />
      </Routes>
      </div>
    </Router>
  );
};

export default App;
